import React from 'react';
import products from '../products'; // Assuming products.js is in the same directory as this component
import { Link } from 'react-router-dom';
import { Row, Col, ListGroup, Image, ListGroupItem, Button } from 'react-bootstrap';

const ProductDetails = ({ match }) => {
    const productId = match.params.id; // Access the id parameter from the URL
    const product = products.find((p) => p._id === productId); // Find the product with the matching ID

    if (!product) {
        return <div>Product not found</div>;
    }

    return (
        <div>
            <Link to="/" className="btn btn-light my-3">
                Go Back
            </Link>
            <Row>
                <Col md={6}>
                    <Image src={product.image} alt={product.name} fluid />
                </Col>
                <Col md={3}>
                    <ListGroup variant='flush'>
                        <ListGroupItem>
                            <h3>{product.name}</h3>
                        </ListGroupItem>
                        <ListGroupItem>
                            <p>{product.description}</p>
                        </ListGroupItem>
                        <ListGroupItem>
                            Price: ${product.price}
                        </ListGroupItem>
                        <ListGroupItem>
                            Stock: {product.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
                        </ListGroupItem>
                        <ListGroupItem>
                            <Button className="btn-block" type="button" disabled={product.countInStock === 0}>
                                Add to Cart
                            </Button>
                        </ListGroupItem>
                    </ListGroup>
                </Col>
            </Row>
        </div>
    );
};

export default ProductDetails;
